class NewsItem {
  final int id;
  final String title;
  final String imgUrl;
  final String category;
  final String author;
  final String time;
  final String? authorImgUrl;
  final String article;
  bool isFavorite;

  NewsItem({
    required this.id,
    required this.title,
    required this.imgUrl,
    required this.category,
    required this.author,
    this.authorImgUrl,
    required this.article,
    this.isFavorite = false,
    this.time = '8 minutes ago',
  });

  bool get isFavoriteNews => isFavorite;

  favoriteToggle() {
    isFavorite = !isFavorite;
  }
}

